mapper.select:
SELECT COUNT(*)
FROM TBL_APPLY a
FROM TBL_APPLY a JOIN TBL_POST p ON a.FK_POST_CODE = p.POST_CODE
WHERE LOWER(p.SUBJECT) LIKE '%'|| LOWER(#{searchWord}) || '%'
FROM TBL_APPLY a JOIN TBL_RESUME r ON a.FK_RESUME_CODE = r.RESUME_CODE JOIN TBL_MEMBER m ON r.FK_USERID = m.USERID WHERE LOWER(m.NAME) LIKE '%'|| LOWER(#{searchWord}) || '%' AND a.STATUS = #{status}
WHERE a.STATUS = #{status}

SELECT V.name AS name, V.resume_subject AS resume_subject, V.post_subject AS post_subject, V.applydate AS applydate, CASE WHEN V.status = '1' THEN '합격' WHEN V.status = '2' THEN '불합격' WHEN V.STATUS = '3' THEN '채용전' END AS status FROM ( SELECT row_number() over(order by a.APPLYDATE desc) AS rno, m.NAME AS name, r.SUBJECT AS resume_subject, p.SUBJECT AS post_subject, a.APPLYDATE, a.STATUS , p.fk_company_id FROM TBL_MEMBER m JOIN TBL_RESUME r ON m.USERID = r.FK_USERID JOIN TBL_APPLY a ON r.RESUME_CODE = a.FK_RESUME_CODE JOIN TBL_POST p ON a.FK_POST_CODE = p.POST_CODE WHERE 1=1 and p.fk_company_id = #{fk_company_id}
AND lower(p.SUBJECT) LIKE '%' || lower(#{searchWord}) || '%'
AND lower(m.NAME) LIKE '%' || lower(#{searchWord}) || '%'
AND a.STATUS = #{status}
) V WHERE rno between #{startRno} and #{endRno} ORDER BY V.APPLYDATE DESC

SELECT RESUME_CODE 
FROM TBL_RESUME
WHERE SUBJECT= #{subject}

SELECT STATUS 
FROM TBL_APPLY
WHERE apply_code = #{apply_code} AND FK_RESUME_CODE = #{resume_code}
